const defaultState={
  totalCount: 0 ,
  defaultCount:10,
  largePauseCount:3,
}

const SET_TOTAL_COUNT ='SET_TOTAL_COUNT'
const ADD_DEFAULT_COUNT ='ADD_DEFAULT_COUNT' 
const REMOVE_DEFAULT_COUNT ='REMOVE_DEFAULT_COUNT' 

const ADD_LARGE_PAUSE_COUNT ='ADD_LARGE_PAUSE_COUNT' 
const REMOVE_LARGE_PAUSE_COUNT ='REMOVE_LARGE_PAUSE_COUNT' 
 
export const totalCountReducer= (state = defaultState, action) => {
  switch (action.type) {

    case SET_TOTAL_COUNT:
      return {...state,totalCount:state.totalCount + action.payload}

      case ADD_DEFAULT_COUNT:
        return {...state,defaultCount:state.defaultCount + action.payload}
      case REMOVE_DEFAULT_COUNT:
        return {...state,defaultCount:state.defaultCount - action.payload}

      // case ADD_LARGE_PAUSE_COUNT:
      //     return {...state,largePauseCount:state.largePauseCount + action.payload}
      // case REMOVE_LARGE_PAUSE_COUNT:
      //     return {...state,largePauseCount:state.largePauseCount - action.payload}
      
    default:
      return state;
  }
};

export const setTotalCountAction=(payload)=>({type:SET_TOTAL_COUNT, payload})
export const addDefaultCountAction=(payload)=>({type:ADD_DEFAULT_COUNT, payload})
export const removeDefaultCountAction=(payload)=>({type:REMOVE_DEFAULT_COUNT, payload})

export const removeLargePauseCountAction=(payload)=>({type:REMOVE_LARGE_PAUSE_COUNT, payload})
export const addLargePauseCountAction=(payload)=>({type:ADD_LARGE_PAUSE_COUNT, payload})
