import React from 'react';
import classes from './NavBtn.module.css'
import {useSelector,useDispatch} from 'react-redux';
import {navSettingsStatus} from '../../store/navBarReducer'

const NavBtn = ({name}) => {
  const dispatch= useDispatch()
  const navSettings=useSelector((state)=>state.navStatus)
  return (
    name==='Настройки'?
    <div className={navSettings.settingStatus?`${classes.nav_active}`:`${classes.nav_disabled}`}  onClick={()=>{dispatch(navSettingsStatus(!navSettings.settingStatus))}}>
      <input type="button" className={classes.btn} value={name}/>
    </div>:
    <div></div>
  )
};

export default NavBtn;
