import React from 'react';
import classes from './style/navSetting.module.css'
import {useSelector,useDispatch} from 'react-redux';
import {addDefaultCountAction,removeDefaultCountAction} from '../../../store/totalCountReducer'


const DefaultCountSetting = (props) => {
  const dispatch= useDispatch()
  const count=useSelector((state)=>state.totalCount)
  
  const addDefaultCount=()=>{
    if(count.defaultCount<1) dispatch(addDefaultCountAction(1))
  }
  const removeDefaultCount=()=>{
    if(count.defaultCount>24)  dispatch(removeDefaultCountAction(1))
  }
  return (
  <div className={classes.timer_setting}>
    <span>{props.tittle}</span>
      <div className={classes.input_wrapper}>
        <span className={classes.minus} onClick={removeDefaultCount}>-</span>
        <span className={classes.input_time}>
        <input type='text' value={count.defaultCount} onChange={(e)=>e.preventDefault} className={classes.input} data-type='number'/>
        </span>
        <span className={classes.plus} onClick={addDefaultCount}> +</span>
      </div>
  </div>
  )
};

export default DefaultCountSetting;
