import React,{useState,useEffect} from 'react';






const date = new Date(150000)
const minutes = date.getMinutes()<10? '0'+date.getMinutes():date.getMinutes()
console.log(minutes)



const Main = () => {

  const[time,setTime]=useState({minutes:25 ,seconds:0})
  const[pause,setPause] = useState({minutes:0 ,seconds:30})

  const[active,setActive]=useState(false)
  const [interv,setInterv]=useState()

  const [totalCount,setTotalCount]=useState(0)
  const [alltime,setAllTime]=useState(0)

  let updateMin= time.minutes, updateSec=time.seconds  

  const timer=()=>{
    setActive(!active)
    run()
    if(!active){
      setInterv(setInterval(run,1))
      clearInterval(interv)
    } else {
      clearInterval(interv)
    }
  } 

  const run = ()=>{
    if(updateMin===0&&updateSec===1) {
      setTotalCount(totalCount+1)
      setTime(pause)
      setActive(false)
      return        
    } 
    if(updateSec===0){
      updateSec=60
      updateMin--;
    }
    updateSec--
    return setTime({minutes:updateMin,seconds:updateSec})
  }


  return <div>

    <div>
      <span>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>:
      <span>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
    </div>

    <div>
      <input type="button" onClick={timer} value='запустить таймер'/>
      <div></div>
      </div>
  </div>;
};

export default Main;
