import React from "react";
import classes from "./ResetCount.module.css";
import {useSelector, useDispatch} from "react-redux";
import {resetTimeAction} from "../../store/timeReducer";
import {setStatusAction} from "../../store/timerStatus";
import {resetPause} from "../../utilites/timerSettings";

const ResetCount = () => {
	const dispatch = useDispatch();
	const status = useSelector((state) => state.status.littlePause);
	const totalCount = useSelector((state) => state.totalCount.totalCount);

	const resetRound = () => {
		dispatch(setStatusAction(false));
		if (!status.littlePause) dispatch(resetTimeAction());
	};

	return (
		<div className={classes.skip_wrapper}>
			{!status ? (
				<span onClick={resetRound}>Обновить таймер</span>
			) : (
				<span onClick={() => dispatch(resetPause({totalCount}))}>
					Обновить паузу
				</span>
			)}
		</div>
	);
};

export default ResetCount;
