import React,{useState} from 'react';

const Main = () => {

  const[time,setTime]=useState({minutes:0,seconds:0})
  

  const timer=()=>{
    run();
    setInterval(run,1000)
  } 

  const run = ()=>{
    let updateMin= time.minutes, updateSec=time.seconds
    if(updateSec===60){
      updateMin++;
      updateSec=0
    }
  }


  return <div onClick={()=>console.log(time)}>

    <div>
      <span>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>:
      <span>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
    </div>

    <div>
      <input type="button" onClick={timer}/>
      </div>
  </div>;
};

export default Main;
