  //     <span className={classes.input_time}>
  //     <input type='text' value={time.minutes} onChange={(e)=>e.preventDefault} className={classes.input} data-type='number'/>{' мин'}
  //     </span>
  //     <span className={classes.plus} onClick={addLittlePause}> +</span>
  //   </div>
  // </div>
  )
};

export default DefaultLitlePauseSetting;
