import React from 'react';
import { useDispatch,useSelector } from 'react-redux';
import {addLikeAction,removeLikeAction} from './store/likeReducer'
import {addItemAction,removeItemAction} from './store/listReducer'

function App() {
  const dispatch = useDispatch()
  const like = useSelector((state)=>state.like.value)
  const list = useSelector((state)=>state.list.list)

  const addLike=() =>{
    dispatch(addLikeAction(1))
  }

  const removeLike=() =>{
    dispatch(removeLikeAction(1))
  }

  const addItem=(name) =>{
    let item={
      name:name,
      id:Date.now(),
    }
    dispatch(addItemAction(item))

  }


  return (
    <div className="App">
      <div>
      <div>{like}</div>
      <input type="button" onClick={()=>addLike()} value='лайк'/>
      <input type="button" onClick={()=>removeLike()}value='дизлайк'/>
      </div>
      <input type="button" onClick={()=>addItem(prompt())} value='создать'/>
      <input type="button" onClick={()=>removeLike()}value='удалить'/>
      <div>
        {list.map((item, i)=>{
          <div>{item.name}</div>
        })}
      </div>
    </div>
  );
}

export default App;
