import { setTimeAction, resetTimeAction,resetLittlePauseTimeAction,resetLargePauseTimeAction } from '../store/timeReducer';
import {setStatusAction,setLittlePauseAction} from '../store/timerStatus'
import {setTotalCountAction} from '../../store/totalCountReducer'

export const defaultTimerSetting = ()=>{
  return (dispatch)=>{
    dispatch(resetTimeAction())
    dispatch(setLittlePauseAction({pause:false}))
    dispatch(setStatusAction({play:true}))
  }
}


