import React from 'react';
import { Helmet } from 'react-helmet-async';
import {useSelector} from 'react-redux';
import work from './icon/work.ico'
import pause from './icon/break.ico'

const Header = () => {
  const timer=useSelector((state)=>state.time.timer)
  const status=useSelector((state)=>state.status)
  const str=`${timer.minutes>=10?timer.minutes:'0'+timer.minutes}:${timer.seconds>=10?timer.seconds:'0'+timer.seconds} · ${status.littlePause?'Перерыв':'Работа'}`
  return (
    <Helmet>
      <title>{str}</title>
    </Helmet>

    
  );
};

export default Header;
