const defaultState={
  play: false,
  littlePause: false,
  largePause: false,
}
const SET_STATUS="SET_STATUS"



export const statusTimerReducer =(state =defaultState, action) => {
  switch (action.type) {
    case SET_STATUS:
      return {...state,pause: action.payload.pause,play: action.payload.play}

    default:
      return state;
  }
};

export const setStatusAction=(payload)=>({type:SET_STATUS, payload})
