const defaultState={
  value:10
}

const ADD_LIKE="addLike"
const REMOVE_LIKE="removeLike"

export const likeReducer =(state=defaultState,action) =>{
  switch(action.type){
    case ADD_LIKE:
      return {...state, like:state.value+action.payload}
    case REMOVE_LIKE:
      return {...state, like:state.value-action.payload}
    default:
      return state
  }
}

export const addLikeAction=(payload)=>({type:ADD_LIKE, payload})
export const removeLikeAction=(payload)=>({type:REMOVE_LIKE, payload})
