import React,{useEffect} from 'react';
import { useDispatch,useSelector } from 'react-redux';
import {addLikeAction,removeLikeAction} from './store/likeReducer'
import {addItemAction,removeItemAction} from './store/listReducer'

// import Main from './components/main';

function App() {
  const dispatch = useDispatch()
  const like = useSelector((state)=>state.like.value)
  const list = useSelector((state)=>state.list.list)

  const addLike=() =>{
    dispatch(addLikeAction(1))
  }

  const removeLike=() =>{
    dispatch(removeLikeAction(1))
  }

  const addItem=(name) =>{
    let item={
      name:name,
      id:Date.now(),
    }
    dispatch(addItemAction(item))
  }
  const removeItem=(item) =>{
    dispatch(removeItemAction(item.id))
  }
  useEffect(() =>console.log("рендер"),[like])


  return (
    <div className="App">
      <div>
      <div>{like}</div>
      <input type="button" onClick={()=>addLike()} value='лайк'/>
      <input type="button" onClick={()=>removeLike()}value='дизлайк'/>
      </div>
      <input type="button" onClick={()=>addItem(prompt())} value='создать'/>
      <div>
        {list.length>0?
        <div>
          {list.map(item =>
            <div onClick={()=>removeItem(item)} key={item.id}>{item.name}</div>
          )}
        </div>
        :<div>ничего нет</div>}
      </div>
      <br/>
      <Main/>
    </div>
  );
}

export default App;
