import React,{useEffect,useState} from 'react';

import {useSelector,useDispatch} from 'react-redux';
import {addDefaultCountAction,removeDefaultCountAction,setDefaultCountAction} from '../../../store/totalCountReducer'
import InputSetting from '../../../UI/InputSetting/InputSetting';

const DefaultCountSetting = (props) => {
  const dispatch= useDispatch()
  const count=useSelector((state)=>state.totalCount)

  // useEffect(() =>{
  //   if(localStorage.getItem('SETTING:DEFAULT_COUNT')) dispatch(setDefaultCountAction(+localStorage.getItem('SETTING:DEFAULT_COUNT')))
  // },[])
  
  // useEffect(() =>{
  //   localStorage.setItem('SETTING:DEFAULT_COUNT',count.defaultCount)
  // },[count.defaultCount])




  const [name,setName]= useLocalStorage('SETTING:DEFAULT_COUNT',count.defaultCount)

  // ========================================
  function useLocalStorage(key, initialValue) {
    console.log(1)
    // состояние для хранения значения
    // передаем функцию инициализации useState для однократного выполнения
    const [storedValue, setStoredValue] = useState(() => {
      try {
        // получаем значение из локального хранилища по ключу
        const item = window.localStorage.getItem(key);
        // разбираем полученное значение или возвращаем initialValue
        return item ? JSON.parse(item) : initialValue;
      } catch (error) {
        // если возникла ошибка, также возвращаем начальное значение
        console.error(error);
        return initialValue;
      }
    });
  
    // возвращаем обернутую версию сеттера useState,
    // которая помещает новое значение в локальное хранилище
    const setValue = (value) => {
      try {
        // значение может быть функцией
        const valueToStore =
          value instanceof Function ? value(storedValue) : value;
        // сохраняем состояние
        setStoredValue(valueToStore);
        // помещаем его в локальное хранилище
        window.localStorage.setItem(key, JSON.stringify(valueToStore));
      } catch (error) {
        // более продвинутая реализация может предполагать обработку ошибок в зависимости от вида ошибки
        console.error(error);
      }
    };
  
    return [storedValue, setValue];
  }

  // ========================================











  

  const addDefaultCount=()=>{
    if(count.defaultCount<24) dispatch(addDefaultCountAction(1))
  }
  const removeDefaultCount=()=>{
    if(count.defaultCount>1)  dispatch(removeDefaultCountAction(1))
  }
  return (
    <InputSetting 
    add={addDefaultCount} 
    remove={removeDefaultCount} 
    value={count.defaultCount} 
    tittle={props.tittle}
    count={true}/>
  )
};

export default DefaultCountSetting;
