import {useEffect} from 'react';
import {useDispatch} from 'react-redux';

export const useLocalStorage=(key, defaultValue,action) =>{
  const dispatch= useDispatch()
  useEffect(() =>{
    const storage =JSON.parse(localStorage.getItem(key))
    if(typeof(storage)==	"boolean")  dispatch(action(JSON.parse(localStorage.getItem(key))))
    if(storage){
      dispatch(action(JSON.parse(localStorage.getItem(key))))
    } else{
      dispatch(action(defaultValue))
    }
  },[])

  useEffect(() =>{
    console.log(typeof(defaultValue),defaultValue)
    localStorage.setItem(key,JSON.stringify(defaultValue))
  },[defaultValue,key])
}
