import React from 'react'
import InputBtn from '../../../UI/InputBtn/InputBtn'
import {useDispatch} from 'react-redux';
import {resetAllTimerAction} from '../../../store/timeReducer'

const LOCAL_SETTINGS= ['SETTING:DEFAULT_TIME','SETTING:LITTLE_PAUSE_TIME','SETTING:LARGE_PAUSE_TIME','SETTING:DEFAULT_COUNT','SETTING:ROUND_COUNT','SETTING:SOUND_STATUS','SETTING:LITTLE_PAUSE_STATUS','DATA:FIRST_TIME_STATUS','DATA:STATISTICS','DATA:TIMER']

const DefaultGlobalReset = (props) => {
  const dispatch= useDispatch()
  const globalReset=()=>{
    LOCAL_SETTINGS.forEach((elem)=>localStorage.removeItem(elem))
    dispatch(resetAllTimerAction())
  }
  
  return (
      <InputBtn tittle={props.tittle} value={props.value} reset={globalReset}/>
  )
}

export default DefaultGlobalReset
