const defaultState={
  timer:{
    minutes:25,
    seconds:0,
  },
  littleBreak:{
    minutes:5,
    seconds:0,
  },
  LargeBreak:{
    minutes:20,
    seconds:0,
  }
}

const SET_TIME="SET_TIME"
const RESET_TIME="RESET_TIME"

export const timeReducer =(state=defaultState,action) =>{
  switch(action.type){
    case SET_TIME:
      return {...state, minutes:action.payload.value1,seconds:action.payload.value2}
    case RESET_TIME:
      return {...state,}
    default:
      return state
  }
}

export const setTimeAction=(payload)=>({type:SET_TIME, payload})
export const resetTimeAction=(payload)=>({type:RESET_TIME, payload})
