import React,{useState,useEffect} from 'react';

const Main = () => {

  const[time,setTime]=useState({minutes:1 ,seconds:0})
  const[active,setActive]=useState(false)
  const [interv,setInterv]=useState()

  const [totalCount,setTotalCount]=useState(0)

  let updateMin= time.minutes, updateSec=time.seconds  

  useEffect(()=>{},[active])



  const timer=()=>{
    setActive(!active)
    if(!active){
      setTimeout(()=>console.log('Готово'),60000)
      run()
      setInterv(setInterval(run,100))
      clearInterval(interv)
    } else {
      clearInterval(interv)
    }
  } 

  const run = ()=>{
    if(updateMin===0&&updateSec===1) {
      setTotalCount(totalCount+1)
      setTime({minutes:1 ,seconds:0})
      setActive(false)
      return 
    } 
    if(updateSec===0){
      updateSec=60
      updateMin--;
    }
    updateSec--
    return setTime({minutes:updateMin,seconds:updateSec})
  }


  return <div>

    <div>
      <span>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>:
      <span>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
    </div>

    <div>
      <input type="button" onClick={timer} value='запустить таймер'/>
      <div>{totalCount}</div>
      </div>
  </div>;
};

export default Main;
