import React,{useState,useEffect} from 'react';
import classes from './NavContentSetting.module.css'
import {useSelector,useDispatch} from 'react-redux';
import {addDefaultTimeAction,deleteDefaultTimeAction} from '../../store/timeReducer'
import FormTimerSetting from '../FormSetting/FormSetting'

const NavContentSetting = () => {
  const dispatch= useDispatch()
  const status=useSelector((state)=>state.status)
  const navSettings=useSelector((state)=>state.navStatus)
  const time=useSelector((state)=>state.time)



const  addTime=()=> dispatch(addDefaultTimeAction(1))
const removeTime=()=> dispatch(deleteDefaultTimeAction(1))



// const change= () =>{
//   if(status.play) return
//   else
//   defaultState.timer.minutes+=1
// }
  return (
    <div className={navSettings.settingStatus?`${classes.navContent_wrapper_active}`:`${classes.navContent_wrapper}`}>
      <div className={classes.setting_wrapper}>
          <FormTimerSetting tittle={'Время работы'} value={time.defaultTime} time={true} add={addTime} remove={removeTime}/>
          <FormTimerSetting tittle={'Маленький перерыв'}value={5} time={true}/>
          <FormTimerSetting tittle={'Большой перерыв'}value={20} time={true}/>
      </div>

      <div className={classes.setting_wrapper}>
          <FormTimerSetting tittle={'Помидорок в раунде'} value={"4 "} time={false}/>
          <FormTimerSetting tittle={'Помидорок за день'}value={"12 "} time={false}/>
      </div>
    </div>

  );
};

export default NavContentSetting;
