import React,{useState,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction } from '../../store/timeReducer';
import {setStatusAction} from '../../store/timerStatus'

import classes from '../Timer/Timer.module.css'


const Timer = () => {
  const dispatch=useDispatch()
  const time=useSelector((state)=>state.time)
  // const status=useSelector((state)=>state.status)
  const [interv,setInterv]=useState(null)


  const [play,setPlay]=useState(false)

  let updateM= time.minutes, updateS=time.seconds  

  useEffect(() => {
    document.title = `${time.minutes>=10?time.minutes:'0'+time.minutes}:${time.seconds>=10?time.seconds:'0'+time.seconds}`
 }, [time]);


  const timer = () => {
    setPlay(!play)
    if(!play){
      setTracer()
      setInterv(setInterval(setTracer,1))
      clearInterval(interv)
    } else {
      clearInterval(interv)
    }
  }
  
 const clear = () => {
   clearInterval(interv)
   setPlay(false)
 }

  const setTracer=()=>{
    if(updateM===0&&updateS===1) {
     dispatch(setTimeAction({value1:25,value2:0}))
      clear()
      setTimeout(()=>console.log(1),1000)
     return
    }
    if(updateS===0){
      updateS=60
      updateM--}
    updateS--
    dispatch(setTimeAction({value1:updateM,value2:updateS}))
    return 
  }


  return (
    <>
  <div>
    <span className={classes.span}>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>
    <span className={classes.span}>:</span>
    <span className={classes.span}>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
  </div>
  {!play?<input type="button" onClick={timer}  value='запустить таймер'/>:
  <input type="button" onClick={clear}  value='остановить таймер'/>}
  </>
  )};

export default Timer;
