import {useEffect} from 'react';
import {useDispatch} from 'react-redux';

export const useLocalStorage=(key, defaultValue,setAction,resetAction) => {
  const dispatch= useDispatch()
  useEffect(() =>{
    const storage =localStorage.getItem(key)
    storage? dispatch(setAction(+localStorage.getItem(key))) : dispatch(resetAction(defaultValue))
  },[])

  useEffect(() =>{
    localStorage.setItem(key,JSON.stringify(defaultValue))
  },[defaultValue])
}
