import React,{useEffect} from 'react';

import {useSelector,useDispatch} from 'react-redux';
import {addDefaultCountAction,removeDefaultCountAction,setDefaultCountAction} from '../../../store/totalCountReducer'
import InputSetting from '../../../UI/InputSetting/InputSetting';

const DefaultCountSetting = (props) => {
  const dispatch= useDispatch()
  const count=useSelector((state)=>state.totalCount)

  useEffect(() =>{
    if(localStorage.getItem('SETTING:DEFAULT_COUNT')) {
      dispatch(setDefaultCountAction(+localStorage.getItem('SETTING:DEFAULT_COUNT')))
    }
  },[])
  

  useEffect(() =>{
    localStorage.setItem('SETTING:DEFAULT_COUNT',count.defaultCount)
  },[count.defaultCount])


  const addDefaultCount=()=>{
    if(count.defaultCount<24) dispatch(addDefaultCountAction(1))
  }
  const removeDefaultCount=()=>{
    if(count.defaultCount>1)  dispatch(removeDefaultCountAction(1))
  }
  return (
    <InputSetting 
    add={addDefaultCount} 
    remove={removeDefaultCount} 
    value={count.defaultCount} 
    tittle={props.tittle}
    count={true}/>
  )
};

export default DefaultCountSetting;
