import React from 'react';
import classes from './style/navSetting.module.css'
import {useSelector,useDispatch} from 'react-redux';
import {addLargePauseAction,deleteLargePauseAction} from '../../../store/timeReducer'


const DefaultLargePauseSetting = (props) => {
  const dispatch= useDispatch()
  const time=useSelector((state)=>state.time.largeBreak)

  const addLargePause=()=>{
    if(time.minutes<30) dispatch(addLargePauseAction(1))
  }

  const removeLargePause=()=>{
    if(time.minutes>1) dispatch(deleteLargePauseAction(1))
  }

  return (
  <div className={classes.timer_setting}>
    <span>{props.tittle}</span>
    {/* <div className={classes.input_wrapper}>
      <span className={classes.minus} onClick={removeLittlePause}>-</span>
      <span className={classes.input_time}>
      <input type='text' value={time.minutes} onChange={(e)=>e.preventDefault} className={classes.input} data-type='number'/>{' мин'}
      </span>
      <span className={classes.plus} onClick={addLittlePause}> +</span>
    </div> */}
  </div>
  )
};

export default DefaultLargePauseSetting;
