const defaultState={
  pause:true,
  play: false,
}
const SET_PAUSE="SET_PAUSE"
const SET_PLAY="SET_PLAY"


export const statusTimerReducer =(state =defaultState, action) => {
  switch (action.type) {
    case SET_PAUSE:
      return {...state,pause: action.payload}
      case SET_PLAY:
        return {...state,play: action.payload}
    default:
      return state;
  }
};

export const setPauseAction=(payload)=>({type:SET_PAUSE, payload})
export const SetPlayAction=(payload)=>({type:SET_PLAY, payload})
