const defaultState={
  totalCount: 0,
  defaultCount:10,
}

const SET_TOTAL_COUNT ='SET_TOTAL_COUNT'
const ADD_DEFAULT_COUNT ='ADD_DEFAULT_COUNT' 
 
export const totalCountReducer= (state = defaultState, action) => {
  switch (action.type) {
    case SET_TOTAL_COUNT:
      return {...state,totalCount:state.totalCount + action.payload}
      case ADD_DEFAULT_COUNT:
        return {...state,totalCount:state.totalCount - action.payload}
    default:
      return state;
  }
};

export const setTotalCountAction=(payload)=>({type:SET_TOTAL_COUNT, payload})
export const addDefaultCountAction=(payload)=>({type:ADD_DEFAULT_COUNT, payload})
