import React,{useEffect,useState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {setSoundStatusAction} from '../../../store/timerStatus'
import RadioInputSetting from '../../../UI/RadioInputSetting/RadioInputSetting';
import useSound from 'use-sound';
import swipe from './sound/bell.mp3'

const DefaultSoundSetting = (props) => {
  const dispatch= useDispatch()
  const status=useSelector((state)=>state.status)

  console.log(status)
  const swipeInput =()=>{
    dispatch(setSoundStatusAction(!status.sound))
  }



    const [playActive] = useSound(
      swipe,
      { volume: 0.4 }
    );

      useEffect(()=>{
        playActive()
      },[status.littlePause])

  return (
  <div>
    <RadioInputSetting tittle={props.tittle} swipe={swipe} status={status}/>
    {/* <button onClick={()=>playActive()}>нажми</button> */}
  </div>);
};

export default DefaultSoundSetting;
