import React,{useState,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction } from '../../store/timeReducer';
import {setStatusAction,setLittlePauseAction} from '../../store/timerStatus'
import {setTotalCountAction} from '../../store/totalCountReducer'
import InputTimer from '../../UI/InputTimer/InputTimer';

import classes from '../Timer/Timer.module.css'


const Timer = () => {
  const dispatch=useDispatch()
  const time=useSelector((state)=>state.time.timer)
  const status=useSelector((state)=>state.status)
  const pause=useSelector((state)=>state.status.littlePause)
  const totalCount=useSelector((state)=>state.totalCount.totalCount)

  let updateM= time.minutes, updateS=time.seconds  

  useEffect(() => {
    document.title=`${time.minutes>=10?time.minutes:'0'+time.minutes}:${time.seconds>=10?time.seconds:'0'+time.seconds}`
 }, [time]);

 const timerPauseSetting=()=>{
  dispatch(setLittlePauseAction({pause:!pause}))
  dispatch(setTimeAction({value1:0,value2:15}))
  dispatch(setTotalCountAction(1))
 }
  // if((totalCount+1)%3===0) {
  //   dispatch(setTimeAction({value1:0,value2:25}))
  //   dispatch(setTotalCountAction(1))
  //   dispatch(setStatusAction({play:true}))
  //   dispatch(setLittlePauseAction({pause:!pause}))
  //   return clearInterval(intervalId)
  // } else


 useEffect(() =>{
   if(status.play){

    const intervalId = setInterval(()=>{
      if(updateM===0&&updateS===1) {
        dispatch(setStatusAction({play:!status.play}))

          if(!pause){
            timerPauseSetting()
            // dispatch(setLittlePauseAction({pause:!pause}))
            // dispatch(setTimeAction({value1:0,value2:15}))
            // dispatch(setTotalCountAction(1))
            return clearInterval(intervalId)
            } else
        dispatch(setTimeAction({value1:0,value2:10}))
        dispatch(setLittlePauseAction({pause:false}))
        dispatch(setStatusAction({play:true}))
        return clearInterval(intervalId)
      }
      if(updateS===0){
        updateS=60
        updateM--}
      updateS--
      dispatch(setTimeAction({value1:updateM,value2:updateS}))
    },500,);
    return ()=>clearInterval(intervalId);
   } return undefined;
 },[status.play,pause,totalCount])


// useEffect(() =>{
  
//   console.log(totalCount%2===0)
// },[totalCount])


  useEffect(()=>{
    if(pause) dispatch(setStatusAction({play:true}))
  },[pause])


  return (
    <div className={classes.timer} >
  <div className={classes.span_wrapper}>
    <span className={classes.span}>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>
    <span className={classes.span}>:</span>
    <span className={classes.span}>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
  </div>
  <div className={classes.img} >
    <div className={classes.wrapper_img} style={{marginLeft:-(time.minutes*60+time.seconds)}}>
    <img src={require('./icon/scale.png')} alt={1}></img>
    </div>
  </div>
  <InputTimer status={status}/>
  </div>
  )};

export default Timer;
