import React,{useState,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction } from '../../store/timeReducer';
import {setStatusAction} from '../../store/timerStatus'


const Timer = () => {
  const dispatch=useDispatch()
  const time=useSelector((state)=>state.time)
  const status=useSelector((state)=>state.status)
  const [interv,setInterv] =useState(null)

  const [play,setPlay]=useState(false)
  const [pause,setPause] = useState(true)


  let updateM= time.minutes, updateS=time.seconds  
  
 
  const tracer=()=>{
    dispatch(setStatusAction({pause:!status.pause,play:!status.play}))
    setPlay(!play)
    
    if(!play){
      setTracer()
      setInterv(setInterval(setTracer,1))
      clearInterval(interv)
      
    } else {
      clearInterval(interv)

    }
  }


  async function timer(){
    setPlay(!play)
    if(!play){
      setTracer()
      setInterv(setInterval(setTracer,1))
      clearInterval(interv)
    } else {
      clearInterval(interv)
      setPlay(true)
    }
    await setPlay(false)
  }


  const setTracer=()=>{
    if(updateM===0&&updateS===1) {
     dispatch(setTimeAction({value1:25,value2:0}))
     return 
      }

    if(updateS===0){
      updateS=60
      updateM--
    }
    updateS--
  
    dispatch(setTimeAction({value1:updateM,value2:updateS}))
    return 
  }



  // const timer=()=>{
  //   setActive(!active)
  //   run()
  //   if(!active){
  //     setInterv(setInterval(run,1))
  //     clearInterval(interv)
  //   } else {
  //     clearInterval(interv)
  //   }
  // } 

  // const run = ()=>{
  //   if(updateMin===0&&updateSec===1) {
  //     setTotalCount(totalCount+1)
  //     setTime(pause)
  //     setActive(false)
  //     return        
  //   } 
  return (
    <>
  <div>
    <span>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>:
    <span>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
  </div>
  <input type="button" onClick={timer} value='запустить таймер'/>
  </>
  )};

export default Timer;
