import React,{useState,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction } from '../../store/timeReducer';
import {setStatusAction} from '../../store/timerStatus'
import InputTimer from '../../UI/InputTimer/InputTimer';

import classes from '../Timer/Timer.module.css'


const Timer = () => {
  const dispatch=useDispatch()
  const time=useSelector((state)=>state.time)
  const status=useSelector((state)=>state.status)
  const [interv,setInterv]=useState(null)


  // const [play,setPlay]=useState(false)

  let updateM= time.minutes, updateS=time.seconds  

  useEffect(() => {
    document.title=`${time.minutes>=10?time.minutes:'0'+time.minutes}:${time.seconds>=10?time.seconds:'0'+time.seconds}`
 }, [time]);


  const runTimer = () => {
    dispatch(setStatusAction({play:!status.play}))
    if(!status.play){
      setTracer()
      setInterv(setInterval(setTracer,1000))
      clearInterval(interv)
    } else {
      clearInterval(interv)
    }
  }
  
 const stopTimer = () => {
   clearInterval(interv)
   dispatch(setStatusAction({play:false}))
 }

  const setTracer=()=>{
    if(updateM===0&&updateS===1) {
     dispatch(setTimeAction({value1:25,value2:0}))
      stopTimer()
     return
    }
    if(updateS===0){
      updateS=60
      updateM--}
    updateS--
    dispatch(setTimeAction({value1:updateM,value2:updateS}))
    return 
  }


  return (
    <div className={classes.timer} onKeyDown={(e) =>{
      if(e.keyCode==='space'){
        console.log(1)
      }
    }}>
  <div className={classes.span_wrapper}>
    <span className={classes.span}>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>
    <span className={classes.span}>:</span>
    <span className={classes.span}>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
  </div>
  <div className={classes.img} >
    <div className={classes.wrapper_img} style={{marginLeft:-(time.minutes*60+time.seconds)}}>
    <img src={require('./icon/scale.png')} alt={1}></img>
    </div>
  </div>

  <InputTimer runTimer={runTimer} stopTimer={stopTimer} status={status}/>
  </div>
  )};

export default Timer;
