const defaultState = []
const SET_STATS = "SET_STATS";
const ADD_STATS = "ADD_STATS";

export const statsReducer = (state = defaultState, action) => {
	switch (action.type) {
		case SET_STATS:
			return {...state, stats: [...action.payload]};
		case ADD_STATS:
			return {...state, stats: [...state.stats, action.payload]};
		default:
			return state;
	}
};

export const setStatsAction = (payload) => ({
	type: SET_STATS,
	payload,
});
export const addStatsAction = (payload) => ({
	type: ADD_STATS,
	payload,
});
