  //  }


  return (
    <div className={classes.skip_wrapper}>
      {status?
      <span onClick={()=>dispatch(defaultTimerSetting())}>Пропустить перерыв</span>:
      <span onClick={()=>dispatch(timerPauseSetting())}>Пропустить раунд</span>}
    </div>
  );
};

export default SkipRound;
