import React from 'react';
import classes from './SkipRound.module.css'
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction, resetTimeAction,resetLittlePauseTimeAction,resetLargePauseTimeAction } from '../../store/timeReducer';
import {setStatusAction,setLittlePauseAction} from '../../store/timerStatus'
import {setTotalCountAction} from '../../store/totalCountReducer'

const SkipRound = () => {
  const dispatch= useDispatch()
  const status =useSelector((state)=>state.status.littlePause)
  const totalCount=useSelector((state)=>state.totalCount.totalCount)

  const timerPauseSetting=()=>{
    if((totalCount+1)%3===0){
      dispatch(setLittlePauseAction({pause:!status.littlePause}))
      dispatch(resetLargePauseTimeAction())
      dispatch(setTotalCountAction(1))    
      return
    }else
      dispatch(setLittlePauseAction({pause:!status.littlePause}))
      dispatch(resetLittlePauseTimeAction())
      dispatch(setTotalCountAction(1))
      return
   }


  const defaultTimerSetting=()=>{
    dispatch(resetTimeAction())
    dispatch(setLittlePauseAction({pause:false}))
    dispatch(setStatusAction({play:true}))
   }

  return (
    <div className={classes.skip_wrapper}>
      {status?
      <span onClick={defaultTimerSetting}>Пропустить перерыв</span>:
      <span>Пропустить раунд</span>}
    </div>
  );
};

export default SkipRound;
