import React,{useState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction } from '../../store/timeReducer';
import {setStatusAction} from '../../store/timerStatus'


const Timer = () => {
  const dispatch=useDispatch()
  const time=useSelector((state)=>state.time)
  const status=useSelector((state)=>state.status)
  const [interv,setInterv] =useState(null)

  let updateM= time.minutes, updateS=time.seconds  
  
 
  const tracer=()=>{
    dispatch(setStatusAction({pause:!status.pause,play:!status.play}))
    
    if(status.pause){
      dispatch(setStatusAction({pause:true,play:false}))
      setTracer()
      setInterv(setInterval(setTracer,1))
      clearInterval(interv)
    } else {
      clearInterval(interv)
    }

  }

  const setTracer=()=>{
    if(updateM===0&&updateS===1) dispatch(setTimeAction({value1:25,value2:0}))
    
    if(updateS===0){
      updateS=60
      updateM--
    }
    updateS--
    return dispatch(setTimeAction({value1:updateM,value2:updateS}))
  }



  // const timer=()=>{
  //   setActive(!active)
  //   run()
  //   if(!active){
  //     setInterv(setInterval(run,1))
  //     clearInterval(interv)
  //   } else {
  //     clearInterval(interv)
  //   }
  // } 

  // const run = ()=>{
  //   if(updateMin===0&&updateSec===1) {
  //     setTotalCount(totalCount+1)
  //     setTime(pause)
  //     setActive(false)
  //     return        
  //   } 
  return (
    <>
  <div>
    <span>{time.minutes>=10?time.minutes:'0'+time.minutes}</span>:
    <span>{time.seconds>=10?time.seconds:'0'+time.seconds}</span>
  </div>
  <input type="button" onClick={tracer} value='запустить таймер'/>
  </>
  )};

export default Timer;
