import React,{useState,useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import { setTimeAction, resetTimeAction,setLittlePauseTimeAction,setLargePauseTimeAction } from '../../store/timeReducer';
import {setStatusAction,setLittlePauseAction} from '../../store/timerStatus'
import {setTotalCountAction} from '../../store/totalCountReducer'

import InputTimer from '../../UI/InputTimer/InputTimer';
import classes from '../Timer/Timer.module.css'


const Timer = () => {
  const dispatch=useDispatch()

  const timer=useSelector((state)=>state.time.timer)
  const time=useSelector((state)=>state.time)

  const status=useSelector((state)=>state.status)
  const totalCount=useSelector((state)=>state.totalCount.totalCount)
  const [firstRun,setFirstRun]=useState(true)

  let updateM= timer.minutes, updateS=timer.seconds  

  useEffect(() => {
    document.title=`${timer.minutes>=10?timer.minutes:'0'+timer.minutes}:${timer.seconds>=10?timer.seconds:'0'+timer.seconds}`
  }, [timer]);

  useEffect(() =>{
    if(timer!==undefined) localStorage.setItem('timerTime',JSON.stringify(timer))
  },[timer])


  useEffect(() =>{
    let t =localStorage.getItem('timerTime')
    if(t){
      dispatch(setTimeAction({minutes:t.minutes, seconds:t.seconds}))
      console.log(t)
    }
  },[time.defaultTime])


  useEffect(()=>{
    if(!status.pause){
      if(firstRun)dispatch(resetTimeAction())
    }
  },[time.defaultTime])





 const timerPauseSetting=()=>{
  if((totalCount+1)%3===0){
    dispatch(setLittlePauseAction({pause:!status.littlePause}))
    dispatch(setLargePauseTimeAction())
    dispatch(setTotalCountAction(1))    
    return
  }else
    dispatch(setLittlePauseAction({pause:!status.littlePause}))
    dispatch(setLittlePauseTimeAction())
    dispatch(setTotalCountAction(1))
    return
 }

 const defaultTimerSetting=()=>{
  dispatch(resetTimeAction())
  dispatch(setLittlePauseAction({pause:false}))
  dispatch(setStatusAction({play:true}))
 }

 useEffect(() =>{
  if(status.play){
    if(firstRun) setFirstRun(false)
    const intervalId = setInterval(()=>{
      if(updateM===0&&updateS===1) {
        dispatch(setStatusAction({play:!status.play}))
        if(!status.littlePause){
          timerPauseSetting()
          return clearInterval(intervalId)
          }else
          defaultTimerSetting()
          return clearInterval(intervalId)
        }
    if(updateS===0){
      updateS=60
      updateM--}
    updateS--
    dispatch(setTimeAction({minutes:updateM,seconds:updateS}))
    },10);
    return ()=>clearInterval(intervalId);
   } return undefined;
 },[status.play,status.littlePause])

  useEffect(()=>{
    if(status.littlePause) dispatch(setStatusAction({play:true}))
  },[status.littlePause])


  return (
    <div className={classes.timer} >
  <div className={classes.span_wrapper}>
    <span className={classes.span}>{timer.minutes>=10?timer.minutes:'0'+timer.minutes}</span>
    <span className={classes.span}>:</span>
    <span className={classes.span}>{timer.seconds>=10?timer.seconds:'0'+timer.seconds}</span>
  </div>
  
  <div className={classes.img} >
    <div className={classes.wrapper_img} style={{marginLeft:-(timer.minutes*60+timer.seconds)}}>
    <img src={require('./icon/scale.png')} alt={1}></img>
    </div>
  </div>
  <InputTimer status={status}/>
  </div>
  )};

export default Timer;
