import {useEffect} from 'react';
import {useDispatch} from 'react-redux';

export const useLocalStorage=(key, defaultValue,action) =>{
  const dispatch= useDispatch()
  useEffect(() =>{
    const storage =localStorage.getItem(key)
    // if(storage||storage===false){
      if(typeof(defaultValue)=="number") dispatch(action(+localStorage.getItem(key)))
       else if(typeof(defaultValue)=="boolean") dispatch(action(Boolean(localStorage.getItem(key))))
    //  else{
    //   dispatch(action(defaultValue))
    // }
  },[])

  useEffect(() =>{
    localStorage.setItem(key,JSON.stringify(defaultValue))
  },[defaultValue,key])
}
