import React,{useEffect,useState} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import {setSoundStatusAction} from '../../../store/timerStatus'
import RadioInputSetting from '../../../UI/RadioInputSetting/RadioInputSetting';
import useSound from 'use-sound';
import swipeSound from './sound/bell.mp3'

const DefaultSoundSetting = (props) => {
  const dispatch= useDispatch()
  const status=useSelector((state)=>state.status)

  const swipeInput =()=>{
    dispatch(setSoundStatusAction(!status.sound))
  }

const st =()=>{
  if(status.sound)return swipeSound,{ volume: 0.4 }
  else return swipeSound,{ volume: 0 }
}

    const [playActive] = useSound(
        swipeSound,
        { volume: 0.4 },
        { soundEnabled: status.sound}
  
    );

    //   useEffect(()=>{
    //   },[status.littlePause])


  // const useMute=()=>{

  //   const [playActive] = useSound()

  //   useEffect(()=>{
      
  //   },[])
  // }

  return (
    <>
    <button onClick={playActive}>кнопка</button>
    <RadioInputSetting
      tittle={props.tittle}
      swipe={swipeInput} 
      status={status.sound}
      value_on={'Вкл'}
      value_off={'Выкл'}
      />
      </>
  );
};

export default DefaultSoundSetting;
