import React from "react";
import classes from "./ResetCount.module.css";
import {useSelector, useDispatch} from "react-redux";
import {
	resetTimeAction,
	resetLittlePauseTimeAction,
	resetLargePauseTimeAction,
} from "../../store/timeReducer";
import {setStatusAction} from "../../store/timerStatus";
import {
	defaultTimerSetting,
	timerPauseSetting,
} from "../../utilites/timerSettings";

const ResetCount = () => {
	const dispatch = useDispatch();
	const status = useSelector((state) => state.status.littlePause);
	const totalCount = useSelector((state) => state.totalCount.totalCount);

	const resetRound = () => {
		dispatch(setStatusAction(false));
		if (!status.littlePause) {
		return	dispatch(resetTimeAction());
		}
    status.littlePause&&(totalCount + 1) % 3 === 0?
		dispatch(resetLargePauseTimeAction()):dispatch(resetLittlePauseTimeAction());
	};

	return (
		<div className={classes.skip_wrapper}>
			{!status ? (
				<span onClick={resetRound}>Обновить таймер</span>
			) : (
				<span onClick={resetRound}>Обновить паузу</span>
			)}
		</div>
	);
};

export default ResetCount;
