import React,{useEffect} from 'react';
import classes from './style/navSetting.module.css'
import {useSelector,useDispatch} from 'react-redux';
import {addDefaultTimeAction,deleteDefaultTimeAction,setDefaultTimeAction} from '../../../store/timeReducer'

const DefaultTimeSetting = (props) => {
  const dispatch= useDispatch()
  const time=useSelector((state)=>state.time.defaultTime)
  console.log(time)

  useEffect(()=>{
    time.minutes?dispatch(setDefaultTimeAction(time.minutes)):dispatch(setDefaultTimeAction(Number(localStorage.getItem('defaultTime'))))
  },[])

  useEffect(()=>{
    console.log(time.minutes)
    localStorage.setItem('defaultTime',time.minutes)
  },[])

  useEffect(()=>{
    console.log(time.minutes)
    localStorage.setItem('defaultTime',time.minutes)
  },[time.minutes])

  const addDefaultTime=()=>{
    if(time.minutes<55)  dispatch(addDefaultTimeAction(1))
  }

  const removeDefaultTime=()=> {
    if(time.minutes>10) dispatch(deleteDefaultTimeAction(1))
  }

  return (
  <div className={classes.timer_setting}>
    <span>{props.tittle}</span>
    <div className={classes.input_wrapper}>
      <span className={classes.minus} onClick={removeDefaultTime}>-</span>
      <span className={classes.input_time}>
      <input type='text' value={time.minutes} onChange={(e)=>e.preventDefault} className={classes.input} data-type='number'/>{' мин'}
      </span>
      <span className={classes.plus} onClick={addDefaultTime}> +</span>
    </div>
  </div>
  )
};

export default DefaultTimeSetting;
