import React,{useEffect,useState} from 'react';

import {useSelector,useDispatch} from 'react-redux';
import {addDefaultCountAction,removeDefaultCountAction,setDefaultCountAction} from '../../../store/totalCountReducer'
import InputSetting from '../../../UI/InputSetting/InputSetting';

const DefaultCountSetting = (props) => {
  const dispatch= useDispatch()
  const count=useSelector((state)=>state.totalCount)

  // useEffect(() =>{
  //   if(localStorage.getItem('SETTING:DEFAULT_COUNT')) dispatch(setDefaultCountAction(+localStorage.getItem('SETTING:DEFAULT_COUNT')))
  // },[])
  
  // useEffect(() =>{
  //   localStorage.setItem('SETTING:DEFAULT_COUNT',count.defaultCount)
  // },[count.defaultCount])

const useLocalStorage=(key, defaultValue) =>{

  const getValue=() =>{
    const storage =localStorage.getItem(key)
    dispatch(setDefaultCountAction(+localStorage.getItem(key)))
    if(storage) return JSON.parse(storage)
    else return defaultValue
  }

  const [value,setValue]=useState(getValue)

  useEffect(() =>{
    localStorage.setItem(key,JSON.stringify(value))
    
  },[value,defaultValue])

  return [value,setValue]
}

const [v1,v2]=useLocalStorage('SETTING:DEFAULT_COUNT', count.defaultCount)

